#ifdef USE_EXTENDED_INDEX_TABLE
/*******************************************************************************************
  Extended index table is used to find the mapping index of a character [0-65535].

  If you can find a simple mathematical expression for index mapping, 
  you can use that, and undefine USE_EXTENDED_INDEX_TABLE.

  If you do not have such a mathematical expression, this index table is just for you.

  To get the index of character 'A', you need to calculate it like the following:

    // a sample function to get the character index on the extended index table.
    USHORT GetExtendedIndex(USHORT glpyh, const FONTHEADER_BFC *pFontHeader)
    {
        if( pFontHeader == NULL )
		      return 0; 

        USHORT firstChar = pFontHeader->firstChar;
        USHORT lastChar  = pFontHeader->lastChar;

        if( glyph < firstChar || glyph > lastChar )
			  return 0;  * not found, return the first glyph index * //

        USHORT index = 0;
        GLYPH_SET *pGlyphSet = pFontHeader->extended_index_table;
        if( pGlyphSet == NULL )
			  return 0; 

        GLYPH_RANGE *pRanges = pGlyphSet->pRanges;
        if( pRanges == NULL )
		      return 0; 

        USHORT index = 0;
        USHORT nRanges = pGlyphset->nRanges;
        for(USHORT i=0; i<nRanges; i++)
        {
            USHORT charLow = pRanges[i].charLow;
            USHORT nGlyphs = pRanges[i].nGlyphs;

            if( glyph < charLow+nGlyphs )
            {
                index += (glyph - charLow); 
                return index;
            }
            else
            {
                index += nGlyphs;
            }
        }

        return 0;   * not found, return the first glyph index * //

    }

 *******************************************************************************************/
const GLYPH_RANGE fontArial8_R_glyph_ranges[] = 
{
/*charLow    nGlyphs     from      to*/
/*=======    =======     =======   =======*/
  {    32,       94 }   /*[0x0020 - 0x007D]*/ 
};

const GLYPH_SET fontArial8_R_glyph_set[] = 
{
    0x10, 	/* cbThis = sizeof(GLYPH_SET) + sizeof(GLYPH_RANGE)*nRanges */
    94, 	/* nChars: total # characters in font */
    1, 		/* nRanges: # ranges */
    (GLYPH_RANGE *)fontArial8_R_glyph_ranges  /* pRanges: pointer to the first range */
};
#endif


